import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import { Divider } from '@material-ui/core';
import CardHeader from '@material-ui/core/CardHeader';
import IconButton from '@material-ui/core/IconButton';
import Avatar from '@material-ui/core/Avatar';
import FileCopyOutlinedIcon from '@material-ui/icons/FileCopyOutlined';
import WarningOutlinedIcon from '@material-ui/icons/WarningOutlined';
import Grid from "@material-ui/core/Grid";

import DateRangeIcon from '@material-ui/icons/DateRange';
import LocalOfferIcon from '@material-ui/icons/LocalOffer';
import UpdateIcon from '@material-ui/icons/Update';
import StoreIcon from '@material-ui/icons/Store';
import InfoIcon from '@material-ui/icons/Info';
import TwitterIcon from '@material-ui/icons/Twitter';
import { Chart } from 'react-charts'
import ChartistGraph from "react-chartist";

const useStyles = makeStyles({
  root: {
    minWidth: 375,
    width:375,
    height:200,
    flexGrow: 1,
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
    color:'#9E42B0',
    textColor:'#9E42B0',
  },
  pos: {
    marginBottom: 12,
  },
  cardCategoryWhite: {
    "&,& a,& a:hover,& a:focus": {
      color: "rgba(255,255,255,.62)",
      margin: "0",
      fontSize: "14px",
      marginTop: "0",
      marginBottom: "0"
    },
    "& a,& a:hover,& a:focus": {
      color: "#FFFFFF"
    }
  },
  cardTitleWhite: {
    color: "#FFFFFF",
    marginTop: "0px",
    minHeight: "auto",
    fontWeight: "300",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    marginBottom: "3px",
    textDecoration: "none",
    "& small": {
      color: "#777",
      fontSize: "65%",
      fontWeight: "400",
      lineHeight: "1"
    }
  }
  ,
  cardCategoryWhite: {
    color: "#ffffff",
    margin: "0",
    fontSize: "14px",
    marginTop: "0",
    marginBottom: "0"
  },
  cardTitle: {
    color: '#000000',
    marginTop: "0px",
    minHeight: "auto",
    fontWeight: "200",
    fontSize:17,
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    marginBottom: "3px",
    textDecoration: "none",
    "& small": {
      color: '#666666',
      fontWeight: "200",
      lineHeight: "1",
      fontSize:17,
    }
  },
  cardCategory:{
    color: '#666666',
    marginTop: "0px",
    minHeight: "auto",
    fontWeight: "200",
    fontSize:13,
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    marginBottom: "3px",
    textDecoration: "none",
    "& small": {
      color: '#666666',
      fontWeight: "200",
      lineHeight: "1",
      fontSize:13,
    }
  },
  cardTitleWhite: {
    color: '#ffffff',
    marginTop: "0px",
    minHeight: "auto",
    fontWeight: "300",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    marginBottom: "3px",
    textDecoration: "none",
    "& small": {
      color: '#666666',
      fontWeight: "400",
      lineHeight: "1"
    }
  }
});

export default function CustomChart() {
  const classes = useStyles();
  const bull = <span className={classes.bullet}>â€¢</span>;

  const simpleLineChartData = {
    labels: ['M', 'T', 'W', 'T', 'F','S'],
    series: [
      [12, 17, 7, 23, 18,38],

    ]
  }

  const data = React.useMemo(
    () => [
      {

        data: [["M", 12], ["T", 17], ["W", 7], ["T", 23],["F", 18],["S", 38]]}]
  )

  const axes = React.useMemo(
    () => [
      { primary: true, type: 'linear', position: 'bottom' },
      { type: 'linear', position: 'left' }
    ],
    []
  )


  return (

    <Grid style={{background:"#EEEEEE",marginLeft:15,marginTop:5,padding:15}} container spacing={3} >
        <Grid style={{ padding: "0 15px !important",marginTop:5}} item xs={12} sm={6} md={3}>
        <Card className={classes.root} variant="outlined">

            <div style={{display:'flex',paddingLeft:10,position:'relative',
            paddingTop:10,paddingBottom:10,flexDirection:'row',textAlign:'justify'}} >

                <div style={{width: '200px',height:'200px',marginRight:20,
                            textAlign: 'left',display: "flex",
                            justifyContent:"center",alignItems: "center",
                            background:"green"}}>
                            <ChartistGraph data={simpleLineChartData} type={'Line'} />
                </div>

            </div>

        </Card>
        </Grid>

        <Grid style={{ padding: "0 15px !important",marginTop:5}} item xs={12} sm={6} md={3}>
        <Card className={classes.root} variant="outlined">

            <div style={{display:'flex',paddingLeft:10,position:'relative',
            paddingTop:10,paddingBottom:10,flexDirection:'row',textAlign:'justify'}} >

                <div style={{width:50,marginRight:20,textAlign: 'left',display: "flex",
                justifyContent:"center",
                            alignItems: "center",height:50,background:"green"}}>
                <StoreIcon style={{ color: "white" }} fontSize="medium"></StoreIcon>
                </div>

                <div style={{background:'#ffffff',
                position:'absolute',bottom:10,right:25
            }}>
                    <p className={classes.cardCategory}>Revenue</p>
                    <h3 className={classes.cardTitle}>
                    $34,245
                    </h3>
                </div>
            </div>
            <Divider/>
            <CardContent style={{paddingLeft:30}}>
            <DateRangeIcon size="small" style={{ color: "#666666" }} ></DateRangeIcon>
                <text style={{marginLeft:10,marginTop:5,fontSize:12,color:"#666666"}} >
                Last 24 Hours
                </text>
            </CardContent>
        </Card>
        </Grid>

        <Grid style={{ padding: "0 15px !important",marginTop:5}} item xs={12} sm={6} md={3}>
        <Card className={classes.root} variant="outlined">

            <div style={{display:'flex',paddingLeft:10,position:'relative',
            paddingTop:10,paddingBottom:10,flexDirection:'row',textAlign:'justify'}} >

                <div style={{width:50,marginRight:20,textAlign: 'left',display: "flex",
                justifyContent:"center",
                            alignItems: "center",height:50,background:"#E8453A"}}>
                <InfoIcon style={{ color: "white" }} fontSize="medium"></InfoIcon>
                </div>

                <div style={{background:'#ffffff',
                position:'absolute',bottom:10,right:25
            }}>
                    <p className={classes.cardCategory}>Fixed Issues</p>
                    <h3 className={classes.cardTitle}>
                    75
                    </h3>
                </div>
            </div>
            <Divider/>
            <CardContent style={{paddingLeft:30}}>
            <LocalOfferIcon size="small" style={{ color: "#666666" }} ></LocalOfferIcon>
