import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import { Divider } from '@material-ui/core';
import CardHeader from '@material-ui/core/CardHeader';
import IconButton from '@material-ui/core/IconButton';
import Avatar from '@material-ui/core/Avatar';
import FileCopyOutlinedIcon from '@material-ui/icons/FileCopyOutlined';
import WarningOutlinedIcon from '@material-ui/icons/WarningOutlined';
import Grid from "@material-ui/core/Grid";

import DateRangeIcon from '@material-ui/icons/DateRange';
import LocalOfferIcon from '@material-ui/icons/LocalOffer';
import UpdateIcon from '@material-ui/icons/Update';
import StoreIcon from '@material-ui/icons/Store';
import InfoIcon from '@material-ui/icons/Info';
import TwitterIcon from '@material-ui/icons/Twitter';
import { Chart } from 'react-charts'
import ChartistGraph from "react-chartist";

const useStyles = makeStyles({
  root: {
    minWidth: 375,
    width:375,
    height:200,
    flexGrow: 1,
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
    color:'#9E42B0',
    textColor:'#9E42B0',
  },
  pos: {
    marginBottom: 12,
  },
  cardCategoryWhite: {
    "&,& a,& a:hover,& a:focus": {
      color: "rgba(255,255,255,.62)",
      margin: "0",
      fontSize: "14px",
      marginTop: "0",
      marginBottom: "0"
    },
    "& a,& a:hover,& a:focus": {
      color: "#FFFFFF"
    }
  },
  cardTitleWhite: {
    color: "#FFFFFF",
    marginTop: "0px",
    minHeight: "auto",
    fontWeight: "300",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    marginBottom: "3px",
    textDecoration: "none",
    "& small": {
      color: "#777",
      fontSize: "65%",
      fontWeight: "400",
      lineHeight: "1"
    }
  }
  ,
  cardCategoryWhite: {
    color: "#ffffff",
    margin: "0",
    fontSize: "14px",
    marginTop: "0",
    marginBottom: "0"
  },
  cardTitle: {
    color: '#000000',
    marginTop: "0px",
    minHeight: "auto",
    fontWeight: "200",
    fontSize:17,
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    marginBottom: "3px",
    textDecoration: "none",
    "& small": {
      color: '#666666',
      fontWeight: "200",
      lineHeight: "1",
      fontSize:17,
    }
  },
  cardCategory:{
    color: '#666666',
    marginTop: "0px",
    minHeight: "auto",
    fontWeight: "200",
    fontSize:13,
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    marginBottom: "3px",
    textDecoration: "none",
    "& small": {
      color: '#666666',
      fontWeight: "200",
      lineHeight: "1",
      fontSize:13,
    }
  },
  cardTitleWhite: {
    color: '#ffffff',
    marginTop: "0px",
    minHeight: "auto",
    fontWeight: "300",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    marginBottom: "3px",
    textDecoration: "none",
    "& small": {
      color: '#666666',
      fontWeight: "400",
      lineHeight: "1"
    }
  }
});

export default function CustomChart() {
  const classes = useStyles();
  const bull = <span className={classes.bullet}>•</span>;

  var delays = 80,
  durations = 500;
var delays2 = 80,
  durations2 = 500;
  const Chartist = require("chartist");
  const dailySalesChart = {
    data: {
      labels: ["M", "T", "W", "T", "F", "S", "S"],
      series: [[10, 15, 20, 25, 27, 30, 35]]
    },
    options: {
      lineSmooth: Chartist.Interpolation.cardinal({
        tension: 0,
        showArea: true,
      }),
      low: 0,
      high: 50, // creative tim: we recommend you to set the high sa the biggest value + something for a better look
      chartPadding: {
        top: 0,
        right: 0,
        bottom: 0,
        left: 0
      }
    },
    // for animation
    animation: {
      draw: function(data) {
        if (data.type === "line" || data.type === "area") {
          data.element.animate({
            d: {
              begin: 600,
              dur: 700,
              from: data.path
                .clone()
                .scale(1, 0)
                .translate(0, data.chartRect.height())
                .stringify(),
              to: data.path.clone().stringify(),
              easing: Chartist.Svg.Easing.easeOutQuint
            }
          });
        } else if (data.type === "point") {
          data.element.animate({
            opacity: {
              begin: (data.index + 1) * delays,
              dur: durations,
              from: 0,
              to: 1,
              easing: "ease"
            }
          });
        }
      }
    }
  };
  return (

    <Grid style={{background:"#EEEEEE",marginLeft:15,marginTop:5,padding:15}} container spacing={3} >
        <Grid style={{ padding: "0 15px !important",marginTop:5}} item xs={12} sm={6} md={3}>
        <Card chart className={classes.root} variant="outlined">

            <div style={{background:"green"}} >
        <ChartistGraph

        style={{color:'white', fontSize: '10px',
        fontFamily:'sans-serif',stroke:'#EEEEEE',strokeWidth: '1',
        strokeDasharray: '1px 1px',
        }}
                className="ct-chart"
                data={dailySalesChart.data}
                type="Line"
                options={dailySalesChart.options}
                listener={dailySalesChart.animation}
              />

            </div>

        </Card>
        </Grid>

        <Grid style={{ padding: "0 15px !important",marginTop:5}} item xs={12} sm={6} md={3}>
        <Card className={classes.root} variant="outlined">

            <div style={{display:'flex',paddingLeft:10,position:'relative',
            paddingTop:10,paddingBottom:10,flexDirection:'row',textAlign:'justify'}} >

                <div style={{width:50,marginRight:20,textAlign: 'left',display: "flex",
                justifyContent:"center",
                            alignItems: "center",height:50,background:"green"}}>
                <StoreIcon style={{ color: "white" }} fontSize="medium"></StoreIcon>
                </div>

                <div style={{background:'#ffffff',
                position:'absolute',bottom:10,right:25
